import { useState } from "react";
function App() {



  let [compteur,setCompteur] = useState(1);
  const handleclick = () => {
    setCompteur(compteur +1) ; 
  }
  const [transactions, setTransactions] = useState([
    { id: 1, nom: "Abricot" },
    { id: 2, nom: "Banane" },
    { id: 3, nom: "P" },
  ]);

  return (
    <div> 
      <h1> Hello </h1>
      <h2> {compteur} </h2> 
      <button onClick={handleclick}>incremente</button>
      <h3> List of Transactions </h3>
        <ul>
          {transactions.map((fruit) => {
            return <li key={fruit.id}> {fruit.nom}</li>;
          })}   
        </ul>
    </div>


  );
}



export default App;
