import { useState } from "react";
function App() {



  let [compteur,setCompteur] = useState(1);
  const handleclick = () => {
    setCompteur(compteur +1) ; 
  }
  const fruits = useState({
    id :1 , nom : "Abricot",
    id : 2 , nom : "Banane",
    id :3 , nom : "P",
  });

  return (
    <div> 
      <h1> Bonjour </h1>
      <h2> {compteur} </h2>
      <button onClick={handleclick}>incremente</button>
      <h3> liste de fruits </h3>
        <ul>
          {fruits.map((fruit) => {
            return <li> {fruit.nom}</li>;
          })}   
        </ul>
    </div>
  );
}


export default App;
