import React, { useState } from 'react';
import TransactionsList from './TransactionList.js';

const App = () => {
  const [walletAddress, setWalletAddress] = useState(''); // Wallet address which can be changed by the user
  const [startBlock, setStartBlock] = useState(0);
  const [displayTransactions, setDisplayTransactions] = useState(false);

  const handleAddressChange = (event) => {
    setWalletAddress(event.target.value);
  };

  const handleStartBlockChange = (event) => {
    const value = event.target.value;
      setStartBlock(parseInt(value));
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    setDisplayTransactions(true);
  };

  return (
    <div>
      <h1>Please enter your wallet address and start block:</h1>

      <form onSubmit={handleFormSubmit}>
        <label htmlFor="walletAddress">Wallet Address:</label>
        <input type="text" id="walletAddress" name="walletAddress" value={walletAddress} onChange={handleAddressChange} />

        <label htmlFor="startBlock">Start Block:</label>
        <input type="number" id="startBlock" name="startBlock" value={startBlock} onChange={handleStartBlockChange} />

        <button type="submit"> Display Transactions</button>
      </form>

      {displayTransactions && <TransactionsList address={walletAddress} startBlock={startBlock} />}
    </div>
  );
};

export default App;
// const walletAddress = '0xDAFEA492D9c6733ae3d56b7Ed1ADB60692c98Bc5'; // Adress to check if it works to put in the app.
