import React, { useState } from 'react';
import TransactionsList from './TransactionList.js';

const App = () => {
  const [walletAddress, setWalletAddress] = useState('');
  const [startBlock, setStartBlock] = useState(0);
  const [showTransactions, setShowTransactions] = useState(false);

  const handleAddressChange = (event) => {
    setWalletAddress(event.target.value);
  };

  const handleStartBlockChange = (event) => {
    setStartBlock(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    setShowTransactions(true);
  };

  return (
    <div>
      <h1>Please enter your wallet address and start block:</h1>

      <form onSubmit={handleFormSubmit}>
        <label htmlFor="walletAddress">Wallet Address:</label>
        <input type="text" id="walletAddress" name="walletAddress" value={walletAddress} onChange={handleAddressChange} />

        <label htmlFor="startBlock">Start Block:</label>
        <input type="number" id="startBlock" name="startBlock" value={startBlock} onChange={handleStartBlockChange} />

        <button type="submit">Show Transactions</button>
      </form>

      {showTransactions && <TransactionsList address={walletAddress} startBlock={startBlock} />}
    </div>
  );
};

export default App;
