import React, { useState, useEffect } from 'react';


const TransactionsList = ({ address, startBlock }) => {
  const [transactions, setTransactions] = useState([]);
  const [status, setStatus] = useState([]);

    console.log(startBlock)
  useEffect(() => {
    const fetchTransactions = async () => {
      const apiKey = "7KT9JFYWU7UIGBUH4TV4QF21PTCE4W7UPN";
      const response = await fetch(`https://api.etherscan.io/api?module=account&action=txlist&address=${address}&startblock=${startBlock}&apikey=${apiKey}`);
      const data = await response.json();
      setTransactions(data.result);
    };
    fetchTransactions();
  }, [address, startBlock]);

  return (
    <div>
      <h2>Transactions List</h2>
        <thead>
          <tr>
            <th>Block Number</th>
            <th>Hash</th>
            <th>From</th>
            <th>To</th>
            <th>Value (ETH)</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.hash}>
              <td>{transaction.blockNumber}</td>
              <td>{transaction.hash}</td>
              <td>{transaction.from}</td>
              <td>{transaction.to}</td>
              <td>{Number(transaction.value) / 10**18}</td>
            </tr>
          ))}
        </tbody>
    </div>
  );
};

export default TransactionsList;
